import React, { useState } from 'react';
import axios from 'axios';
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from 'mdbreact';



const Login = (props) => {


    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(null);


    const handlelogin = () =>{
        // props.history.push('/dashboard');

        axios({
            method: "POST",
            withCredentials: true,
            data: {
                username: username, 
                password: password, 
            },
            url: "http://localhost:3001/getdata",
          }).then(response => {
            console.log("When clicked handlelogin");
            setLoading(false);
            if(response.data.message === "Success") {
                props.history.push("/Dashboard");                 
            } else if(response.data.message === "Failure"){
                props.history.push('/');
            }else{
                console.log("F");
            }
            console.log('response >>>>>>>>>>', response.data);
        }).catch(error =>{
            setLoading(false);
            if(error.message.status === 401 || error.message.status === 400){
                setError(error.response.data.message);
            }
            else{
                setError("Something went wrong. Please try again later.");
            }
            console.log('error >>>>>>', error);
        });

    }

    return (
        <MDBContainer>
          <MDBRow>
            <MDBCol md="6">
              <form>
                <p className="h5 text-center mb-4">Sign in</p>
                <div className="grey-text">
                  <MDBInput label="Type your email" icon="envelope" group validate error="wrong"
                    success="right" type="text" value={username}  onChange={e => setUsername(e.target.value)}/>
                  <MDBInput label="Type your password" icon="lock" group validate type="password"
                 value={password}
                 onChange={e => setPassword(e.target.value)} />
                </div>
                <div className="text-center">
                  <MDBBtn onClick={handlelogin}>Login</MDBBtn>
                </div>
              </form>
            </MDBCol>
          </MDBRow>
        </MDBContainer>
        );
}

export default Login;
